import React from "react";

// for the deleting method we need to delete by ID
function Transaction({id,date,description,category,amount,transactions,setTransactions,url}){

  const deleteTransaction=()=>{
    fetch(url/`${id}`,{
      method:'DELETE'
    })
    .then((resp) => resp.json())
    .then (() => {
      const newTransaction = transactions.filter((transaction)=> {return transaction.id !== id})
      setTransactions(newTransaction)
    })
  }
  return (
    <tr>
      <td>{date}</td>
      <td>{description}</td>
      <td>{category}</td>
      <td>{amount}</td>
      {/* <button></button> */}
    </tr>
  );
}

export default Transaction;
