import React, { useState } from "react";


const url = "http://localhost:8001/transactions";
function AddTransactionForm({ transactions, setTransactions }) {
  const [addTransactionForm, setAddTransactionForm] = useState(
    {
  //   // date: date,    this doent work the page goes blank
  //   // description: description,
  //   // category: category,
  //   // amount: amount,

  //   // date,  this doesnt work also
  //   // description,
  //   // category,
  //   // amount,

    date: "",
    description: "",
    category: "",
    amount: 0,
  }
  );

  // onchange
  const handleChange = (e) => {
    let descrip = e.target.descrip;
    let value = e.target.value;
    setAddTransactionForm({...addTransactionForm, [descrip]: value });
  };

  const postTransaction = () => {
    fetch(url, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify( 
        {
          addTransactionForm :{
            date: date
          }
        }
        // {...addTransactionForm,
        // date,
        // description,
        // category,
        // amount}
      ),
    })
      .then((respon) => respon.json())
      .then((data) => {
        // console.log(data + "y isnt console logging")
        const newData = [...transactions, data];
        // console.log("hafsa"+newData)
        setTransactions(newData);
      });
  };

  const handleSubmit = (e)=>{
    e.preventDefault()
    postTransaction()
  }

  return (
    <div className="ui segment">
      <form className="ui form" onSubmit={handleSubmit} >
        <div className="inline fields">
          <input type="date" name="date" onChange={handleChange} />
          <input
            type="text"
            name="description"
            placeholder="Description"
            onChange={handleChange}
          />
          <input
            type="text"
            name="category"
            placeholder="Category"
            onChange={handleChange}
          />
          <input
            type="number"
            name="amount"
            placeholder="Amount"
            step="0.01"
            onChange={handleChange}
          />
        </div>
        <button className="ui button" type="submit">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
