import React, { useState } from "react";


const url = "http://localhost:8001/transactions";
function AddTransactionForm({ transactions, setTransactions }) {
  const [addTransactionForm, setAddTransactionForm] = useState(
    {
  //   // date: date,    this doent work the page goes blank
  //   // description: description,
  //   // category: category,
  //   // amount: amount,

  //   // date,  this doesnt work also
  //   // description,
  //   // category,
  //   // amount,

    date: "",
    description: "",
    category: "",
    amount: '',
  }
  );
  // const {data,description,category,amount} = addTransactionForm

  // onchange
  const handleChange = (e) => {
    // let descrip = e.target.descrip;
    // let value = e.target.value;
    // setAddTransactionForm({
    //   ...addTransactionForm,
    //   [e.target.descrip]: e.target.value,
    // });
    // setAddTransactionForm({...addTransactionForm, [descrip]: value });
    //  tryinging
    const newData = {...addTransactionForm}
    newData[e.target.desc] = e.target.value 
    setAddTransactionForm(newData)

  };

  // POST
  const postTransaction = () => {
    fetch(url, {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify( addTransactionForm
        // {
        //   addTransactionForm :{
        //     date: date,
        //     description:description,
        //     category:category,
        //     amount: amount
        //   }
        // }
        // {...addTransactionForm,
        // date,
        // description,
        // category,
        // amount}
      ),
    })
      .then((respon) => respon.json())
      .then((data) => {
        // console.log(data + "y isnt console logging")
        const newData = [...transactions, data];
        // console.log("hafsa"+newData)
        setTransactions(newData);
      });
  };

  const handleSubmit = (e)=>{
    e.preventDefault()
    // console.log(addTransactionForm)
    postTransaction()
    // console.log(postTransaction)
  }

  return (
    <div className="ui segment">
      <form className="ui form" onSubmit={handleSubmit}>
        <div className="inline fields">
          <input
            type="date"
            name="date"
            value={addTransactionForm.date}
            onChange={(e) => handleChange(e)}
          />
          <input
            type="text"
            name="description"
            value={addTransactionForm.description}
            placeholder="Description"
            onChange={(e) => handleChange(e)}
          />
          <input
            type="text"
            name="category"
            value={addTransactionForm.category}
            placeholder="Category"
            onChange={(e) => handleChange(e)}
          />
          <input
            type="number"
            name="amount"
            value={addTransactionForm.amount}
            placeholder="Amount"
            step="0.01"
            onChange={(e) => handleChange(e)}
          />
        </div>
        <button className="ui button" type="submit">
          Add Transaction
        </button>
      </form>
    </div>
  );
}

export default AddTransactionForm;
